import{_,c as t,o as a,ag as o}from"./chunks/framework.BDwTZuFy.js";const n=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"docs/stead3-ru/Общие-сведения/Как-выглядит-классическая-INSTEAD-игра.md","filePath":"docs/stead3-ru/Общие-сведения/Как-выглядит-классическая-INSTEAD-игра.md"}'),i={name:"docs/stead3-ru/Общие-сведения/Как-выглядит-классическая-INSTEAD-игра.md"};function l(r,e,s,p,d,m){return a(),t("div",null,e[0]||(e[0]=[o('<h3 id="как-выглядит-классическая-instead-игра" tabindex="-1">Как выглядит классическая INSTEAD игра <a class="header-anchor" href="#как-выглядит-классическая-instead-игра" aria-label="Permalink to &quot;Как выглядит классическая INSTEAD игра&quot;">​</a></h3><p>Итак, как выглядит классическая INSTEAD игра?</p><p><em>Главное окно</em> игры содержит описательную часть, информацию о статической и динамической части сцены, активные события и картинку сцены (в графическом интерпретаторе) с возможными переходами в другие сцены.</p><p><em>Описательная часть сцены</em> отображается только один раз, при показе сцены, или при явном осмотре сцены (в графическом интерпретаторе -- <em>Статическая часть сцены</em> содержит информацию о статических объектах сцены (обычно, это декорации) и отображается всегда. Эта часть написана автором игры.</p><p><em>Динамическая часть сцены</em> составлена из описаний объектов сцены, которые присутствуют в данный момент на сцене. Эта часть генерируется автоматически и отображается всегда. Обычно, в ней представлены объекты, которые могут менять своё местоположение.</p><p>Игроку доступны объекты, доступные на любой сцене -- <em>инвентарь</em>. Игрок может взаимодействовать с объектами инвентаря и действовать объектами инвентаря на другие объекты сцены или инвентаря.</p><blockquote><p>Следует отметить, что понятие инвентаря является условным. Например, в &quot;инвентаре&quot; могут находиться такие объекты как &quot;открыть&quot;, &quot;осмотреть&quot;, &quot;использовать&quot; и т.д.</p></blockquote><p><em>Действиями</em> игрока могут быть:</p><ul><li>осмотр сцены;</li><li>действие на объект сцены;</li><li>действие на объект инвентаря;</li><li>действие объектом инвентаря на объект инвентаря;</li><li>действие объектом инвентаря на объект сцены;</li><li>переход в другую сцену.</li></ul>',9)]))}const c=_(i,[["render",l]]);export{n as __pageData,c as default};
